#Attribut un theme a un mot
setThemeWord = lambda x : (x, choice(themes))
setThemeWord2 = lambda x : (x, randint(0, 100000) % len(themes))

def generateDistriDirect(sizeDoc, sizeTopic):
    topics = list(range(sizeTopic))
    choices = topics
    r = topics
    for i in range(sizeDoc - len(topics)):
        rand = choice(choices)
        r.append(rand)
        choices.append(rand)
        del choices[-1]
    return r

#Attribut un theme a tous les mots d'un texte
def setDocsThemeWord(docs, d=1, themes=5):
    r = {}
    if d == 1:
        for index, doc in enumerate(docs):
            r["Doc" + str(index)] = dict(map(setThemeWord,doc))
    elif d == 2:
        ddd = [generateDistriDirect(i, themes) for i in list(map(lambda x: len(x) ,docs))]
        for index, doc in enumerate(docs):
            r["Doc" + str(index)] = { w:t for w,t in zip(docs[index],ddd[index])}
    else:
        for index, doc in enumerate(docs):
            r["Doc" + str(index)] = dict(map(setThemeWord2,doc))
    return r




#Calcul la probabilite d'un theme par rapport au doc
def getProbTopicDoc(docs, themes):
    r = {}
    for doc, words in docs.items():
        r[doc] = {theme: list(words.values()).count(theme) / len(words) for theme in themes}
    return r


# Donne un theme a un document par rapport a la plus forme probabilité
def assignTopicDoc(probaDoc):
    r = {}    
    for doc, prob in probaDoc.items():
        r[doc] = sorted(prob.items(), key=lambda x: x[1], reverse=True)[0][0]
    return r

#Calcule le nombre total de theme dans le corpus
def setSumTopic(docs, topics):
    r = { theme: 0 for theme in topics}
    for doc, wordTopic in docs.items():
        for i in list((wordTopic.values())):
            r[i] += 1
    return r

# Compte le nombre de mot dans chaque theme
def getCountWordTopic(docs, themes):
    r = {}
    for doc, wordTopic in docs.items():
        for w, t in wordTopic.items():
            if w not in r.keys():
                r[w] = { theme: 0 for theme in themes}
            r[w][t] += 1
    return r
 
#Calculer la probabilite qu'un mot soit dans un theme
def getProbaWordTopic(docs, themes):
    r = {}
    sumTopic = setSumTopic(docs, themes)
    wordTopic = getCountWordTopic(docs, themes)
    for word, count in wordTopic.items():
        r[word] = {topic : countWord / countWordByTopic for topic, countWordByTopic, countWord in zip(sumTopic.keys(), sumTopic.values(), count.values())}
    return r
    

def assignTopicWordByProb(docs):
    r = {}
    for word, prob in docs.items():
        r[word] = list(sortDictDesc(prob).keys())[0]
    return r

    
def getNewProbTopic(docsProbaTopic, wordsProbaTopic, docsWithWords, themes):
    r = {}
    for docName, probaTopic in docsProbaTopic.items():
        r[docName] = {theme: sum([wordsProbaTopic[w][theme] * probaTopic[theme] for w in list(docsWithWords[docName].keys())]) for theme in themes}
    return r


def assignTopicDocToWords(docs, words):
    r = {}
    for doc, topic in docs.items():
        r[doc] = {key: topic for key in words[doc].keys()}
    return r
    
def getBestWordLDA(labels, words):
    r = {}
    for doc, topic in labels.items():
        if topic not in r.keys():
            r[topic] = []
        for word, label  in words[doc].items():
            if topic == label:
                r[topic].append(word)
    return r

def myLDA(docs, themes):
    moyenne = 0
    getOnlyValues = lambda x: np.array(list(x.values()))
    sizeDocs = len(docs)
    docsSet = setDocsThemeWord(docs, 2, len(themes))
    probDocs = getProbTopicDoc(docsSet, themes)
    probWords = getProbaWordTopic(docsSet, themes)
    labelDocs = assignTopicDoc(probDocs)
    while(moyenne < 1):
        d = {}
        for doc in docsSet.keys():
            r = {}
            for key in docsSet[doc].keys():
                r[key] = list(sortDictDesc({t: c for t,c in zip(themes, np.array(list(probWords[key].values())) * np.array(list(probDocs[doc].values())))}).keys())[0]
            d[doc] = r
        
        newProbaTopicDocs = getProbTopicDoc(d, themes)
        newAssignTopic = assignTopicDoc(newProbaTopicDocs)
        moyenne = sum(getOnlyValues(labelDocs) == getOnlyValues(newAssignTopic)) / sizeDocs
        docsSet = d
        probDocs = getProbTopicDoc(docsSet, themes)
        probWords = getProbaWordTopic(docsSet, themes)
        labelDocs = newAssignTopic
        
    return newAssignTopic, docsSet

def getAllWords(docs):
    r = []
    for doc in docs:
        r += doc
    return list(set(r))

def getDocsToVec(vectors, docs):
    r = []
    for doc in docs:
        d = [0] * len(vectors)
        for word in doc:
            d[vectors.index(word)] = 1
        r.append(d)
    return r

def getSimilarity(cosData, x, y):
    return cosData[x][y]


def getDocIndex(data, themes):
    r = [[] for _ in range(len(themes))]
    for index, (name, theme) in enumerate(data.items()):
        r[theme].append(index)
    return r

def getAllCombiDocIndex(docs):
    return [list(combinations(i,2)) for i in docs]

def calculSimiliraty(lda, data, themes):
    r = []
    cosData = cosine_similarity(data)
    docIndex = getDocIndex(lda, themes)
    allC = getAllCombiDocIndex(docIndex)
    for cos in allC:
        r.append(sum([getSimilarity(cosData, a, b) for a, b in cos]))
    return r


