#Attribut un theme a tous les mots d'un texte
def setDocsThemeWord(docs, d=1):
    r = {}
    if d == 1:
        for index, doc in enumerate(docs):
            r["Doc" + str(index)] = dict(map(setThemeWord,doc))
    else:
        for index, doc in enumerate(docs):
            r["Doc" + str(index)] = dict(map(setThemeWord2,doc))
    return r
        
